con:config.script:
today=new Date ()
def expiryDate= today+1
return expiryDate.format ('dd-MM-yyyy HH:mm:ss')

con:configuration.scriptText:
// http://www.soapui.org/apidocs/com/eviware/soapui/support/XmlHolder.html
import com.eviware.soapui.support.XmlHolder
// http://docs.oracle.com/javase/7/docs/api/java/text/SimpleDateFormat.html
import java.text.SimpleDateFormat
// http://docs.oracle.com/javase/7/docs/api/java/util/regex/Pattern.html
import java.util.regex.Pattern
// http://docs.oracle.com/javase/7/docs/api/java/util/regex/Matcher.html
import java.util.regex.Matcher
// http://docs.oracle.com/javase/7/docs/api/java/lang/Math.html
import java.lang.Math

// http://docs.oracle.com/javase/6/docs/api/org/w3c/dom/Node.html?is-external=true
// http://docs.oracle.com/javase/7/docs/api/java/util/Date.html


def holder = new XmlHolder( messageExchange.responseContentAsXml )
holder.namespaces["ns2"] = "http://protocol.airsim.services.hxc/"
def node = holder.getDomNode( "//ns2:getSmsHistoryResponse[1]/return[1]/text[1]" )

assert node != null

def node_text = node.getFirstChild().getNodeValue()

log.info( "node_text = " + node_text )

Pattern pattern = Pattern.compile( "^Your account will expire on\\s+(\\S+.*\\S+).*\$" );
Matcher matcher = pattern.matcher( node_text );

assert matcher.matches();

log.info( "matcher.group( 1 ) = " + matcher.group( 1 ) )

SimpleDateFormat formatter = new SimpleDateFormat("dd-MM-yyyy HH:mm:ss")
def date = formatter.parse( matcher.group( 1 ) )
def date_ts = date.getTime()
//log.info( "date_ts = " + date_ts )
def expiry_date = context.expand( '${expiry_date#result}' )
//log.info( "expiry_date = " + expiry_date )
def expiry_date_ts = formatter.parse( expiry_date ).getTime()
//log.info( "expiry_date_ts = " + expiry_date_ts )
//log.info( "expiry_date_ts - date_ts = " + ( expiry_date_ts - date_ts ) )

assert ( Math.abs( expiry_date_ts - date_ts ) / 1000 ) < 59
