import groovy.time.TimeCategory

today= new Date() //represents the date and time when it is created

use (TimeCategory) {
    weekstime = today + 40.seconds
}

//return weekstime.format('yyyy-MM-dd')+"T00:00:00.000+02:00"


return weekstime.format("yyyy-MM-dd'T'HH':'mm':'ss")+".000+02:00"



return now;

