package hxc.utils.protocol.hsx;

public class ResponseHeader
{
	// This parameter reference to a single operation, generated by the system it was initiated
	// from.
	// Note: Each operation must have a unique value.
	// The value in this parameter must be unique per operation and can be a sequence
	// number. An operation in this case is e.g. one getSubscriberInformation.
	// Mandatory
	public String originTransactionId;

	// This parameter is the identity of the system user or the session from where the operation
	// was initiated. It might be used for security management or logging purposes for an
	// example.
	// Optional
	public String originOperatorId;

	// This parameter is sent back after a message has been processed and indicates
	// completion status of the message.
	// Mandatory
	public int responseCode;

	// This parameter is sent back after a message has been processed and describes the
	// completion status.
	// Optional
	public String responseMessage;

	// completionStatus
	public final static int COMPLETE_SUCCESS = 0;
	public final static int COMPLETE_MAYRETRY = 1;
	public final static int COMPLETE_NORETRY = 2;
	public final static int COMPLETE_REJECT = 3;

	// systemComponentId
	public final static int COMPONENT_BASE = 0;
	public final static int COMPONENT_SGW = 100;

	// reason
	public final static int REASON_SUCCESS = 0;
	public final static int REASON_INTERNAL = 0;

	public void setResponseCode(int completionStatus, int systemComponentId, int reason)
	{
		responseCode = (completionStatus * 1000000) + (systemComponentId * 1000) + reason;
	}

}
